mapper.select:
SELECT asset_id assetId , ca.continent_id continentId , ca.village_id villageId , village_number villageNumber , token_name tokenName , description , cn.img_url imgUrl , village_type villageType ,cac.user_id userId , exists (select village_nft_id from map_parcel where village_nft_id = cn.nft_id) isDeployed FROM cardano_asset ca left outer join cardano_nft cn on ca.village_number = cn.param1 left outer join cardano_account cac on ca.stake_address = cac.stake_address left outer join village_type vt on vt.village_type_id = cn.village_type_id where cac.user_id = #{userId} and ca.village_number is not null
AND ca.continent_id = #{continentId}
AND ca.village_id = #{villageId}
and 1 = 1
and exists (select village_nft_id from map_parcel where village_nft_id = cn.nft_id )
and not exists (select village_nft_id from map_parcel where village_nft_id = cn.nft_id )

SELECT asset_id assetId , continent_id continentId , village_id villageId , token_name tokenName , description , img_url imgUrl , parcel_x parcelX , parcel_y parcelY ,cac.user_id userId FROM cardano_asset ca left outer join cardano_nft cn on ca.parcel_x = cn.param6 and ca.parcel_y = cn.param7 left outer join cardano_account cac on ca.stake_address = cac.stake_address where cac.user_id = #{userId} and ca.parcel_x is not null and ca.parcel_y is not null
AND ca.continent_id = #{continentId}
AND ca.village_id = #{villageId}
