mapper.select:
select mp.map_parcel_id mapParcelId ,mp.continent_id continentId ,mp.village_id villageId ,mp.user_id userId ,u.nick_name nickName ,u.twitter twitter ,u.facebook facebook ,u.discord discord ,vt.village_type villageType ,mp.user_img_url userImgUrl ,mp.parcel_x_3d parcelX ,mp.parcel_y_3d parcelY ,mp.moon_onoff moonOnoff ,cn.description description ,cn.img_url nftImgUrl ,vt.img_url villageTypeUrl ,IF(mp.user_img_url is not null,mp.user_img_url,IF(cn.img_url is not null,cn.img_url,cn2.img_url)) popupImgUrl from map_parcel mp left outer join cardano_nft cn on mp.village_nft_id = cn.nft_id left outer join cardano_nft cn2 on mp.land_nft_id = cn2.nft_id left outer join user u on u.user_id = mp.user_id left outer join village_type vt on cn.village_type_id = vt.village_type_id where 1=1
AND mp.continent_id = #{continentId}
AND mp.village_id = #{villageId}

select mp.map_parcel_id mapParcelId ,mp.continent_id continentId ,mp.village_id villageId ,mp.user_id userId ,u.nick_name nickName ,u.twitter twitter ,u.facebook facebook ,u.discord discord ,vt.village_type villageType ,mp.user_img_url userImgUrl ,mp.parcel_x_3d parcelX ,mp.parcel_y_3d parcelY ,mp.land_type landType ,cn.description description ,cn.img_url nftImgUrl ,vt.img_url villageTypeUrl ,IF(mp.user_img_url is not null,mp.user_img_url,IF(cn.img_url is not null,cn.img_url,cn2.img_url)) popupImgUrl from map_parcel mp left outer join cardano_nft cn on mp.village_nft_id = cn.nft_id left outer join cardano_nft cn2 on mp.land_nft_id = cn2.nft_id left outer join user u on u.user_id = mp.user_id left outer join village_type vt on cn.village_type_id = vt.village_type_id where 1=1
AND mp.parcel_x_3d = #{parcelX}
AND mp.parcel_y_3d = #{parcelY}
AND mp.continent_id = #{continentId}
AND mp.village_id = #{villageId}
